import groovy.json.JsonSlurper
                            import groovy.json.JsonBuilder
                            import groovy.json.JsonOutput

                            def ff = session.get()
                            if(!ff)return
                            ff = session.write(ff, {rawIn, rawOut->;
                            // transform streams into reader and writer
                            rawIn.withReader("UTF-8"){reader->;
                            rawOut.withWriter("UTF-8"){writer->;
                            def slurper = new JsonSlurper() as Object
                            def fileContent = slurper.parse(reader)
                            def flowFileName = ff.getAttribute("filename")
                            def cloudEvent = JsonOutput.toJson([
                            specversion: '0.3',
                            id: UUID.randomUUID().toString(),
                            type: "com.rdsea.nifi.mqtt.app.error",
                            source: "NiFi Flow/processor/PutHDFS",
                            time: new Date().format("yyyy-MM-dd'T'HH:mm:ss.SSSX"),
                            subject: "${fileContent.device_id}-${fileContent.time}",
                            log: "Could not persist File($flowFileName) to remote HDFS",
                            error: "Could not persist File($flowFileName) to remote HDFS",
                            data: fileContent
                            ])
                            def resultJson = new JsonSlurper().parseText(cloudEvent)
                            new JsonBuilder(resultJson).writeTo(writer)
                            }
                            }
                            } as StreamCallback)
                            ff = session.putAttribute(ff, "FLUENTD_TAG", "com.rdsea.nifi.mqtt.app.error")
                            session.transfer(ff, REL_SUCCESS)

