import groovy.json.JsonSlurper
                            import groovy.json.JsonBuilder

                            def ff = session.get()
                            if(!ff)return
                            ff = session.write(ff, {rawIn, rawOut->
                            // transform streams into reader and writer
                            rawIn.withReader("UTF-8"){reader->
                            rawOut.withWriter("UTF-8"){writer->
                            //parse reader into Map
                            def json = new JsonSlurper().parse(reader)
                            //change/set values
                            json.prov.wasGeneratedBy = "nifi-PutHDFS"
                            json.prov.wasDerivedFrom = json.prov.previousId
                            json.prov.previousId = "nifi-PutHDFS"
                            json.prov.type = "sensorDataFile"
                            //write changed object to writer
                            new JsonBuilder(json).writeTo(writer)
                            }
                            }
                            } as StreamCallback)
                            session.transfer(ff, REL_SUCCESS)

